        inv_scale2 = inv_scale[ j + 1];
        scale1     = scale    [ j + 4];
        SM_ELEMENT_D( J, j + 1, j + 4) *= inv_scale2*scale1;

        
        

        

        
 	    
        
        // H2_2 by He_1
        
        // because the Jacobian is initialized to zeros by default
        // SM_ELEMENT_D( J, j + 1, j + 5 ) = 0;
        
        

        

        
 	    
        
        // H2_2 by He_2
        
        // because the Jacobian is initialized to zeros by default
        // SM_ELEMENT_D( J, j + 1, j + 6 ) = 0;
        
        

        

        
 	    
        
        // H2_2 by He_3
        
        // because the Jacobian is initialized to zeros by default
        // SM_ELEMENT_D( J, j + 1, j + 7 ) = 0;
        
        

        

        
 	    
        
