        //
        
        
        // H_1 by H2_1
        
        SM_ELEMENT_D( J, j + 2, j + 0 ) = k11[i]*H_2 + 2*k12[i]*de + 2*k13[i]*H_1 - 2*k21[i]*H_1*H_1;
